CREATE OR ALTER PROCEDURE EDIT_LOC (
    lid double precision,
    idac double precision,
    idc double precision,
    selfweght double precision,
    idt double precision,
    inz smallint,
    state double precision,
    squat double precision,
    bench double precision,
    dl double precision,
    loc double precision)
returns (
    coef double precision,
    ts timestamp)
as
declare variable idcategory double precision;
declare variable idwilks double precision;
declare variable res double precision;
declare variable sex smallint;
declare variable asex smallint;
declare variable aclub char(30);
declare variable atrener char(30);
declare variable range numeric(12,0);
BEGIN
  res = null;
  asex = 2;
  TS = CURRENT_TIMESTAMP;
  res = :lid;

  select sex
    , club
    , trener
  from lifter
  where id_lifter = :lid
  into :sex
    , :aclub
    , :atrener;

  select id_range
  from competit c
    left join lifter_on_competition loc on c.id_compet=loc.id_compet
  where loc.id_lifter_compet = :loc
  into :range;

  if (sex = 1) then
    asex = 3;

  select min(id_category)
  from weigth_category
  where we >= :"SELFWEGHT"
    and (sex = :aSex or sex = 1)
  into :idcategory;

  select id_wilks
    , coef
  from wilks
  where self_weight >= :"SELFWEGHT" and
    sex = :sex
  order by self_weight
  rows 1
  into :idwilks
    , :coef;

  update lifter_on_competition set
    id_category = :idcategory,
    id_compet   = :"IDC",
    id_wilks    = :idwilks,
    id_stream   = :"STATE",
    club        = :aclub,
    trener      = :atrener,
    id_team     = :"IDT",
    InZach      = :"INZ",
    id_agecl    = :"IDAC",
    EDITTIME    = :"TS"
  where
    ID_LIFTER_COMPET = :LOC;

  if (coalesce(:bench, 0) = 0) then
  begin
    bench = :selfweght;
    if (:range = 4) then
      bench = :bench /2;
    bench = ceiling(:bench/2.5)*2.5;
  end

  update lifter_exersis
  set squat1 = :squat
    , bench1 = :bench
    , dl1 = :dl
    , EDITTIME = :ts
  where ID_LIFTER_COMPET = :loc;

  idcategory = NULL;
  idwilks = NULL;
  res = NULL;
  sex = NULL;
  asex = NULL;
  ACLUB = NULL;
  ATRENER = NULL;
  range = NULL;

  POST_EVENT 'LOC_EDIT';
END
^
SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER PROCEDURE ADD_LOC (
    lid numeric(12),
    idac numeric(12),
    idc numeric(12),
    selfweght double precision,
    idt numeric(12),
    zr integer,
    inz smallint,
    state double precision,
    squat double precision,
    bench double precision,
    dl double precision)
returns (
    loc numeric(12),
    coef double precision,
    ts timestamp)
as
declare variable idcategory numeric(12);
declare variable idwilks numeric(12);
declare variable res numeric(12);
declare variable sex smallint;
declare variable asex smallint;
declare variable aclub char(30);
declare variable atrener char(30);
declare variable range integer;
BEGIN
  LOC = GEN_ID(LOC, 1);
  res = null;
  asex = 2;
  TS = CURRENT_TIMESTAMP;
  res = :Lid;
  
  select sex
    , club
	, trener
  from lifter
  where id_lifter = :lid
  into :sex
    , :aclub
    , :atrener;

  if (sex = 1) then
    asex = 3;

  select min(id_category)
  from weigth_category
  where we >= :SELFWEGHT and
    (sex = :aSex or sex = 1)
  into :idcategory;

  select id_wilks
    , coef
  from wilks
  where self_weight >= :SELFWEGHT and
    sex = :sex
  order by self_weight
  rows 1
  into :idwilks
    , :coef;

  select id_range
  from competit c
    left join lifter_on_competition loc on c.id_compet = loc.id_compet
  where loc.id_lifter_compet = :loc
  into :range;

  insert into lifter_on_competition
    (
      ID_LIFTER_COMPET,      id_category,
      id_lifter,             id_compet,
      id_wilks,              id_stream,
      club,                  trener,
      id_team,               zr,
      InZach,                id_agecl,
      EDITTIME,              CREATETIME)
  values
    (
      :LOC,                  :idcategory,
      :LID,                  :IDC,
      :idwilks,              :STATE,
      :aclub,                :atrener,
      :IDT,                  :ZR,
      :INZ,                  :IDAC,
      :TS,                   CURRENT_TIMESTAMP
    );

  if (state = -1) then
    if (coalesce(:bench, 0) = 0) then
    begin
       bench = :selfweght;
       if (:range = 4) then
         bench = :bench /2;
       bench = ceiling(:bench/2.5)*2.5;
    end

  insert into lifter_exersis
    (
      ID_LIFTER_COMPET,
      squat1,
      bench1,
      dl1,
      STOY,
      EDITTIME
    )
  values 
    (
      :LOC,
      :SQUAT, 
      :BENCH, 
      :DL, 
      '0',
      :"TS"
    );
    
  idcategory = NULL;
  idwilks = NULL;
  res = NULL;
  sex = NULL;
  asex = NULL;
  ACLUB = NULL;
  ATRENER = NULL;
  RANGE = NULL;

  POST_EVENT 'LOC_ADD';
END
^
SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER PROCEDURE BESTBENCHBYLOC (
    loc double precision)
returns (
    bench char(10))
as
declare variable bench_ numeric(9,1);
declare variable num numeric(9,1);
BEGIN
  select  bench1 * bench2, bench2
  from  lifter_exersis le
  where coalesce(oc5, 0) =1
    and le.id_lifter_compet = :Loc
  into :bench_, :num;

  IF ((bench_ IS NULL) or (num < 8))  THEN
    bench = 0;
  ELSE
    bench = ceiling(:bench_/2.5)*2.5;

  bench_= NULL;
  num = NULL;
  SUSPEND;
END
^
SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER PROCEDURE CURRENTTOTALL (
    loc numeric(12))
returns (
    totall double precision)
as
declare variable bestsquat numeric(9,1);
declare variable bestbench numeric(9,1);
declare variable bestdl numeric(9,1);
declare variable ocsquat integer;
declare variable ocbench integer;
declare variable ocdl integer;
declare variable range numeric(12);
BEGIN

  select cast(squat as numeric(9,1))
  from bestsquatbyloc(:LOC)
  into :BESTSQUAT;

  select cast(bench as numeric(9,1))
  from bestbenchbyloc(:LOC)
  into :BESTBENCH;

  select cast(DL as numeric(9,1))
  from bestdlbyloc1(:LOC)
  into :BESTDL;

  select coalesce(oc1, 0)+coalesce(oc2, 0)+coalesce(oc3, 0) as ocsquat
    , /*coalesce(oc4, 0)+*/ coalesce(oc5, 0) /*+coalesce(oc6, 0) */as ocbench
    , coalesce(oc7, 0)+coalesce(oc8, 0)+coalesce(oc9, 0) as ocdl
    , r.kind
  from lifter_on_competition loc 
    inner join competit c on loc.id_compet = c.id_compet 
    inner join lifter_exersis le on le.id_lifter_compet = loc.id_lifter_compet 
    inner join range r on r.id_range =c.id_range
  where loc.id_lifter_compet = :LOC
  into :ocsquat, :ocbench, :ocdl, :range;

  totall = 0;

  if (range = 1) then
    if ((ocbench > 0) and (ocsquat > 0) and (ocdl > 0)) then
      TOTALL =  :BESTSQUAT + :BESTBENCH + :BESTDL;

  if ((range = 2) and (ocbench > 0)) then
      TOTALL =  :BESTBENCH;

  if ((range = 3) and (ocdl > 0)) then
      TOTALL =  :BESTDL;

  ocsquat = null;
  ocbench = null;
  ocdl = null;
  BestSquat = null;
  BestBench = null;
  BestDl = null;
  range = null;

  SUSPEND;
END
^
SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER PROCEDURE ABSOLUTE ( idc numeric(12)) returns ( cat varchar(25), fio varchar(100), totall double precision, gloss double precision, sex integer, agename varchar(15), agenum integer, loc numeric(12)) as BEGIN for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex, ab.loc,'Teenagers' as agename, 1 as agenum from absolutebyage(:"IDC") ab where ageclass in (13) and sex = 0 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend;
for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex , ab.loc,'Junior ', 2 from absolutebyage(:"IDC") ab where ageclass = (20) and sex = 0 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend;
for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex , ab.loc,'Open ', 3 from absolutebyage(:"IDC") ab where ageclass = (-2) and sex = 0 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend;
for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex , ab.loc,'Masters ', 4 from absolutebyage(:"IDC") ab where ageclass in (40, 50, 60) and sex = 0 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend;
for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex, ab.loc,'Teenagers' as agename, 1 as agenum from absolutebyage(:"IDC") ab where ageclass in (13) and sex = 1 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend;
for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex , ab.loc,'Junior ', 2 from absolutebyage(:"IDC") ab where ageclass = (20) and sex = 1 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend;
for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex , ab.loc,'Open ', 3 from absolutebyage(:"IDC") ab where ageclass = (-2) and sex = 1 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend;
for select ab.catname, ab.fio, ab.totall, ab.gloss, ab.sex , ab.loc,'Masters ', 4 from absolutebyage(:"IDC") ab where ageclass in (40, 50, 60) and sex = 1 order by 5,8,4 desc rows 24 into :"CAT", :"FIO", :"TOTALL", :"GLOSS", :"SEX", :"LOC", :"AGENAME", :"AGENUM" do suspend; END ^ SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER PROCEDURE ABSOLUTE_GRAM ( idc numeric(12)) returns ( place integer, loc numeric(12), rusfio varchar(50), engfio varchar(50), wc varchar(20), totall double precision, gloss double precision, sex integer, age_name varchar(50), age_num numeric(12,0), agecnamerus varchar(50), agecnameeng varchar(50)) as declare variable idac varchar(50); declare variable num integer; begin
idac = ''; num = 1;
FOR SELECT loc.id_lifter_compet, ab.cat, cast(Cutfio2(ab.fio) as varchar(50)) as rusfio, cast(Transliter(Cutfio2(ab.fio)) as varchar(50)) as Engfio, ab.totall, ab.gloss, ab.sex, ab.agename, ab.agenum, ac.name as rusagedetname, ac.engname as engagedetname FROM absolute(:IDC) ab inner join lifter_on_competition loc on ab.loc = loc.id_lifter_compet inner join age_class ac on loc.id_agecl = ac.id_agecl ORDER BY sex , agenum , gloss desc INTO loc , wc , rusfio , engfio , totall, gloss , sex , age_name, age_num, agecnamerus, agecnameeng DO BEGIN if (IDAC <> age_name||sex) then begin NUM = 1; IDAC = :age_name||:sex; end
if (NUM < 4) then begin PLACE = NUM; NUM = NUM+1; SUSPEND; end END
idac = NULL; num = NULL;
END ^ SET TERM ; ^

SET TERM ^ ;
