CREATE OR ALTER PROCEDURE ADD_LOC (
    lid numeric(12,0),
    idac numeric(12,0),
    idc numeric(12,0),
    selfweght double precision,
    idt numeric(12,0),
    zr integer,
    inz smallint,
    state double precision,
    squat double precision,
    bench double precision,
    dl double precision)
returns (
    loc numeric(12,0),
    coef double precision,
    ts timestamp)
as
declare variable idcategory numeric(12,0);
declare variable idwilks numeric(12,0);
declare variable res numeric(12,0);
declare variable sex smallint;
declare variable asex smallint;
declare variable aclub char(30);
declare variable atrener char(30);
declare variable range integer;
BEGIN
  LOC = GEN_ID(LOC, 1);
  res = null;
  asex = 2;
  TS = CURRENT_TIMESTAMP;
  res = :Lid;
  
  select sex
    , club
    , trener
  from lifter
  where id_lifter = :lid
  into :sex
    , :aclub
    , :atrener;

  if (sex = 1) then
    asex = 3;

  select min(id_category)
  from weigth_category
  where we >= :SELFWEGHT and
    (sex = :aSex or sex = 1)
  into :idcategory;

  select id_wilks
    , coef
  from wilks
  where self_weight >= :SELFWEGHT and
    sex = :sex
  order by self_weight
  rows 1
  into :idwilks
    , :coef;

  select id_range from competit c where id_compet = :idc into :range;

  insert into lifter_on_competition
    (
      ID_LIFTER_COMPET,      id_category,
      id_lifter,             id_compet,
      id_wilks,              id_stream,
      club,                  trener,
      id_team,               zr,
      InZach,                id_agecl,
      EDITTIME,              CREATETIME)
  values
    (
      :LOC,                  :idcategory,
      :LID,                  :IDC,
      :idwilks,              :STATE,
      :aclub,                :atrener,
      :IDT,                  :ZR,
      :INZ,                  :IDAC,
      :TS,                   CURRENT_TIMESTAMP
    );

  if (state = -1) then
    if (coalesce(:bench, 0) = 0) then
    begin
       bench = :selfweght;
       if (:range = 4) then
         bench = :bench /2;
       bench = ceiling(:bench/2.5)*2.5;
    end

  insert into lifter_exersis
    (
      ID_LIFTER_COMPET,
      squat1,
      bench1,
      dl1,
      STOY,
      EDITTIME
    )
  values 
    (
      :LOC,
      :SQUAT, 
      :BENCH, 
      :DL, 
      '0',
      :"TS"
    );
    
  idcategory = NULL;
  idwilks = NULL;
  res = NULL;
  sex = NULL;
  asex = NULL;
  ACLUB = NULL;
  ATRENER = NULL;
  RANGE = NULL;

  POST_EVENT 'LOC_ADD';
END
^
SET TERM ; ^

