CREATE OR ALTER PROCEDURE ADD_TOWN (
    country varchar(3),
    region varchar(50),
    name varchar(50)
)
returns (
    id NUMERIC(12),
    ts timestamp)
as
begin
 ID = GEN_ID(TOWNS, 1);
 TS = CURRENT_TIMESTAMP;

 INSERT INTO TOWNS
   (ID, COUNTRY, REGION, NAME, EDITTIME)
 VALUES
   (:ID, :COUNTRY, :REGION, :NAME, :TS);
 POST_EVENT 'TOWN_ADD';
 POST_EVENT 'ADD';
end
^
SET TERM ; ^

