TS = CURRENT_TIMESTAMP;
INSERT INTO DELETED (ID, "TABLE", EDITTIME, "VALUE") VALUES (GEN_ID(DELETED, 1), 'TOWNS', :"TS", :"ID");
POST_EVENT 'TOWN_DELETE'; POST_EVENT 'DELETE'; end ^ SET TERM ; ^

SET TERM ^ ; CREATE OR ALTER PROCEDURE EDIT_TOWN ( id NUMERIC(12), country varchar(3), region varchar(50), name varchar(50) ) returns ( ts timestamp) as begin TS = CURRENT_TIMESTAMP;
UPDATE TOWNS SET COUNTRY = :COUNTRY , REGION = :REGION , NAME = :NAME , EDITTIME = :TS WHERE ID = :ID;
POST_EVENT 'TOWN_EDIT'; POST_EVENT 'EDIT'; end ^ SET TERM ; ^

