CREATE OR ALTER PROCEDURE ADD_COMPET ( id_range numeric(12), name char(250), town varchar(160), description char(200), date_begin date, date_end date, level_ integer, grp numeric(12)) returns ( id numeric(12), ts timestamp) as BEGIN
ID = GEN_ID(COMPET, 1); TS = CURRENT_TIMESTAMP;
INSERT INTO COMPETIT ( ID_COMPET, ID_RANGE, NAME, TOWN, DESCRIPTION, DATE_BEGIN, DATE_END, "LEVEL", EDITTIME, GRP ) VALUES ( :ID, :ID_RANGE, :NAME, :TOWN, :DESCRIPTION,:DATE_BEGIN, :DATE_END, :LEVEL_, :TS, coalesce(:GRP, :ID) );
post_event 'COMPET_ADD'; post_event 'ADD'; END^
CREATE OR ALTER PROCEDURE EDIT_COMPET ( id_range NUMERIC(12), name char(250), town varchar(160), description char(200), date_begin date, date_end date, level_ integer, id_compet NUMERIC(12)) returns ( ts timestamp ) AS declare variable grp numeric(12); BEGIN TS = CURRENT_TIMESTAMP;
UPDATE COMPETIT SET ID_RANGE = :ID_RANGE, NAME = :NAME, TOWN = :TOWN, DESCRIPTION = :DESCRIPTION, DATE_BEGIN = :DATE_BEGIN, DATE_END = :DATE_END, "LEVEL" = :"LEVEL_", EDITTIME = CURRENT_TIMESTAMP WHERE ID_COMPET = :ID_COMPET;
SELECT grp FROM COMPETIT WHERE ID_COMPET = :ID_COMPET INTO GRP;
UPDATE COMPETIT SET NAME = :NAME, TOWN = :TOWN, DESCRIPTION = :DESCRIPTION, "LEVEL" = :"LEVEL_", EDITTIME = CURRENT_TIMESTAMP WHERE grp = :GRP;
GRP = NULL; post_event 'COMPET_EDIT'; post_event 'EDIT'; END^
SET TERM ; ^

SET TERM ^ ; CREATE OR ALTER PROCEDURE EDIT_LIFTER ( id NUMERIC(12), name char(60), sex smallint, phone varchar(20), dat_bith date, adress varchar(200), town varchar(160), id_razr NUMERIC(12), club char(30), trener char(30), regards char(500), email varchar(50), aname char(60) ) returns ( ts timestamp ) as BEGIN
UPDATE LIFTER SET name = :NAME WHERE id_lifter = :ID and ((:NAME <> '' AND name <> :NAME) OR Name is Null);
UPDATE LIFTER SET email = :EMAIL WHERE id_lifter = :"ID" and ((:EMAIL <> '' AND email <> :EMAIL) OR email is Null);
UPDATE LIFTER SET sex = :SEX WHERE id_lifter = :ID and :SEX <> -1 AND sex <> :sex;
UPDATE LIFTER SET Phone = :PHONE WHERE id_lifter = :ID and ((:PHONE <> '' AND Phone <> :PHONE) OR Phone is Null);
UPDATE LIFTER SET dat_bith = :DAT_BITH WHERE id_lifter = :ID and dat_bith <> :dat_bith;
UPDATE LIFTER SET adress = :ADRESS WHERE id_lifter = :ID and ((:ADRESS <> '' AND adress <> :ADRESS) OR adress is Null);
UPDATE LIFTER SET Town = :TOWN WHERE id_lifter = :ID and ((:TOWN <> '' AND Town <> :TOWN) OR Town is Null);
UPDATE LIFTER SET id_razr = :ID_RAZR WHERE id_lifter = :ID and :ID_RAZR <> -1 AND id_razr <> :id_razr;
UPDATE LIFTER SET Club = :CLUB WHERE id_lifter = :ID and ((:CLUB <> '' AND Club <> :CLUB) OR Club is Null);
UPDATE LIFTER SET Trener = :TRENER WHERE id_lifter = :ID and ((:TRENER <> '' AND Trener <> :TRENER) OR Trener is Null);
UPDATE LIFTER SET Regards = :REGARDS WHERE id_lifter = :ID and ((:REGARDS <> '' AND Regards <> :REGARDS) OR Regards is Null);
UPDATE LIFTER SET ANAME = :ANAME WHERE id_lifter = :ID and ((:ANAME <> '' AND ANAME <> :ANAME) OR ANAME is Null);
TS = CURRENT_TIMESTAMP;
UPDATE LIFTER SET EDITTIME = :TS WHERE ID_LIFTER = :ID;
POST_EVENT 'LIFTER_EDIT'; POST_EVENT 'EDIT'; END ^ SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER PROCEDURE COMPETSTAT (
    idc NUMERIC(12))
returns (
    NAME VARCHAR(150),
    "VALUE" VARCHAR(50))
as
begin
  NAME = 'Кол-во спортсменов общее';
  SELECT COUNT(ID_LIFTER_COMPET)
  FROM LIFTER_ON_COMPETITION
  WHERE ID_COMPET = :"IDC"
  INTO :"VALUE";
  suspend;

  NAME = 'Кол-во спортсменов уник.';
  SELECT COUNT(distinct ID_LIFTER)
  FROM LIFTER_ON_COMPETITION
  WHERE ID_COMPET = :IDC
  INTO :"VALUE";
  suspend;

  NAME = 'Кол-во команд участниц';
  SELECT COUNT(distinct ID_TEAM)
  FROM LIFTER_ON_COMPETITION
  WHERE ID_COMPET = :IDC
  INTO :"VALUE";
  suspend;

  NAME = 'Кол-во городов спортсменов';
  select count(distinct(l.town))
  from lifter_on_competition loc join
    Lifter L on l.id_lifter = loc.id_lifter
  where loc.id_compet = :"IDC"
  into :"VALUE";
  suspend;
  
  NAME = 'Кол-во рекордов национальных';
  select NUM from RECORDSINCOMPET(:IDC, 1) into :"VALUE";
  suspend;

  NAME = 'Кол-во рекордов европейских';
  select NUM from RECORDSINCOMPET(:IDC, 2) into :"VALUE";
  suspend;

  NAME = 'Кол-во рекордов мировых';
  select NUM from RECORDSINCOMPET(:IDC, 3) into :"VALUE";
  suspend;

  NAME = 'Спортсмены - самый пожилой';
  select TRIM(l.name)||'('||
    ((cast('now' as date) - l.dat_bith)/365)||
    ') '||', '||TRIM(l.town)
  from lifter_on_competition loc join
    Lifter L on l.id_lifter = loc.id_lifter
  where loc.id_compet = :IDC
  order by l.dat_bith asc
  rows 1 
  into :"VALUE";
  suspend;

  NAME = 'Спортсмены - самый молодой';
  select  TRIM(l.name)||'('||
    ((cast('now' as date) - l.dat_bith)/365)||
    ') '||', '||TRIM(l.town)
  from lifter_on_competition loc join
    Lifter L on l.id_lifter = loc.id_lifter
  where loc.id_compet = :IDC
  order by  l.dat_bith desc
  rows 1 
  into :"VALUE";
  suspend;

end^

SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER PROCEDURE COMPETITIONWINNERS2 (
    idc numeric(12,0))
returns (
    place integer,
    fio varchar(150),
    totall varchar(12),
    gloss double precision,
    ageclass varchar(25),
    sex integer,
    loc numeric(12,0),
    idcateg numeric(12,0),
    categname char(10))
as
declare variable cwc char(25);
declare variable cidac char(25);
declare variable i integer;
begin
  cWC = '';
  cIDAC = '';
  I = 1;

  FOR
  SELECT
    FIO, TOTALL, PTS, AGECLASS, SEX, LOC, WCATEG
  FROM
    FINALREPORT2(:IDC)
  WHERE
    coalesce(TOT, 0) > 0
  into :"FIO", :"TOTALL", :"GLOSS",  :"AGECLASS", :"SEX", :"LOC", :CATEGNAME
  do
  begin
    if ((:cWC <> :CATEGNAME ) or (:cIDAC <> :AGECLASS)) then
    begin
      i = 1;
      cWC =  :CATEGNAME;
      cIDAC = :AGECLASS;
    end

    if (:i < 4) then
    begin
      PLACE = :i;
      i = :i + 1;
      suspend;
    end
  end

  cWC = NULL;
  cIDAC = NULL;
  I = NULL;
end

^
SET TERM ; ^

SET TERM ^ ;

