CREATE OR ALTER procedure GET_STREAM_DESCR (
    IDC numeric(12,0),
    IDS numeric(12,0))
returns (
    DESCR varchar(512))
as
declare variable SEX integer;
declare variable AGENAME varchar(150);
declare variable WCNAME varchar(150);
declare variable ASEX integer;
declare variable AAGENAME varchar(150);
begin
 descr = '';
 aSex = -1;
 aAgeName = '';


 for select tbl.sex, tbl.name as ageName, wc.name as wcName
    from (
        select l.sex, ac.name, wc.we
        from lifter_on_competition loc
          inner join lifter l on l.id_lifter = loc.id_lifter
          inner join age_class ac on ac.id_agecl = loc.id_agecl
          inner join weigth_category  wc on wc.id_category = loc.id_category
        where id_compet = :idc and id_stream = :ids
        group by l.sex, ac.name, wc.we
    ) tbl
    inner join weigth_category  wc on wc.we = tbl.we
     order by tbl.sex, tbl.name, wc.we
    into :sex, :agename, :wcname
  do begin
    if (sex <> asex) then
    begin
      if (asex > -1) then
        descr = :descr ||'|';
      if (sex = 1) then
        descr = :descr || 'Мужчины: ';
      if (sex = 0) then
        descr = :descr || 'Женщины: ';
      asex = :sex;
    end

    if (ageName <> aAgeName) then
    begin
      if (substring(:descr from CHAR_LENGTH(:descr)-1) = ',') then
        descr = substring(:descr from 1 for CHAR_LENGTH(:descr)-2)||'; ';

      descr = :descr || Trim(:ageName)||': ';
      aagename = :agename;
    end

    descr = :descr ||Trim(:wcname)||', ';
  end

  if (substring(:descr from CHAR_LENGTH(:descr)-1) = ',') then
    descr = substring(:descr from 1 for CHAR_LENGTH(:descr)-2);

 descr = trim(:descr);
 suspend;
 aSex     = null;
 aAgeName = null;
 sex      = null;
 ageName  = null;
 wcName   = null;
end
^
SET TERM ; ^

SET TERM ^ ; CREATE OR ALTER PROCEDURE DO_UP as begin UPDATE AGE_CLASS SET NAME = 'Юноши 13 - 19', FROM_AGE = 1, TO_AGE = 19, ENGNAME = 'Teen 13-19' WHERE (ID_AGECL = 13);
UPDATE AGE_CLASS SET NAME = 'Юниоры 20 - 23', FROM_AGE = 20, TO_AGE = 23, ENGNAME = 'Juniors' WHERE (ID_AGECL = 20);
UPDATE AGE_CLASS SET NAME = 'Открытая', FROM_AGE = 24, TO_AGE = 39, ENGNAME = 'Open' WHERE (ID_AGECL = -2);
UPDATE AGE_CLASS SET NAME = 'Мастера 40 - 49', FROM_AGE = 40, TO_AGE = 49, ENGNAME = 'Masters 40-49' WHERE (ID_AGECL = 40);
UPDATE AGE_CLASS SET NAME = 'Саб. мастера 33 - 39', FROM_AGE = 33, TO_AGE = 39, ENGNAME = 'Sub Masters 33-39' WHERE (ID_AGECL = -4);
UPDATE AGE_CLASS SET NAME = 'Мастера 50 - 59', FROM_AGE = 50, TO_AGE = 59, ENGNAME = 'Masters 50-59' WHERE (ID_AGECL = 50);
UPDATE AGE_CLASS SET NAME = 'Мастера 60+', FROM_AGE = 60, TO_AGE = 999, ENGNAME = 'Masters 60+' WHERE (ID_AGECL = 60); end ^ SET TERM ; ^

