SET TERM ^ ; CREATE OR ALTER procedure SELECT_RECORDS ( LOC numeric(12,0), EXERSCISE integer) returns ( ID_LIFTER numeric(12,0), ID_AGECL numeric(12,0), WEGHT double precision, SOREVN_NAME varchar(200), "LEVEL" integer) as declare variable TODAY date; declare variable ID_CATEGORY numeric(12,0); declare variable ID_COMPET numeric(12,0); declare variable ID_RANGE numeric(12,0); declare variable LIFTER_SEX integer; declare variable LIFTER_DATEBITH date; declare variable LEVLOW integer; declare variable LEVHIGH integer; declare variable FIX integer; declare variable LVL numeric(12,0); BEGIN TODAY = CAST('NOW' AS DATE);
SELECT LOC.ID_COMPET, LOC.ID_CATEGORY, C.ID_RANGE, L.SEX, c."LEVEL", T.FIXRECORD FROM LIFTER_ON_COMPETITION LOC JOIN COMPETIT C ON C.ID_COMPET= LOC.ID_COMPET JOIN LIFTER L ON LOC.ID_LIFTER= L.ID_LIFTER JOIN TEAM T on T.ID_TEAM = LOC.ID_TEAM WHERE LOC.ID_LIFTER_COMPET = :LOC INTO :ID_COMPET, :ID_CATEGORY, :ID_RANGE, :LIFTER_SEX, :LevHigh, :LEVLOW;
FOR SELECT "LEVEL" as lvl FROM PLEVEL where "LEVEL" between :LEVLOW and :LEVHIGH order by 1 desc INTO :LVL do begin for SELECT r.ID_LIFTER, r.ID_AGECL, WEGHT, SOREVN_NAME, "LEVEL" FROM ( select ID_RECORD, id_lifter, ID_CATEGORY, ID_RANGE, id_agecl,"LEVEL" ,ID_EXER, WEGHT, SOREVN_NAME from RECORDS union all select ID_RECORD, id_lifter, ID_CATEGORY, ID_RANGE, id_agecl,"LEVEL" ,ID_EXER, WEGHT, SOREVN_NAME from pRecord(:EXERSCISE, :ID_CATEGORY, :ID_RANGE) ) R JOIN LIFTER L ON L.ID_LIFTER = R.ID_LIFTER JOIN AGE_CLASS AC ON ac.id_agecl=r.id_agecl WHERE ID_CATEGORY = :ID_CATEGORY AND ID_RANGE = :ID_RANGE AND ID_EXER = :EXERSCISE AND L.SEX = :LIFTER_SEX AND R."LEVEL" = :lvl AND r.ID_AGECL IN ( SELECT ID_AGECL FROM AGE_CLASS WHERE ( SELECT (:TODAY - L.DAT_BITH) / 365 FROM LIFTER_ON_COMPETITION LOC INNER JOIN LIFTER L on loc.id_lifter = l.id_lifter WHERE LOC.ID_LIFTER_COMPET = :LOC ) BETWEEN FROM_AGE AND TO_AGE OR ID_AGECL = -2 ) AND R.ID_RECORD in (SELECT ID_RECORD FROM MAX_RECORDS(:id_range, :id_category, :lifter_sex, :exerscise)) ORDER BY ac.from_age, "LEVEL" INTO :ID_LIFTER, :ID_AGECL, :WEGHT, :SOREVN_NAME, :"LEVEL" do SUSPEND; end
TODAY = NULL; ID_CATEGORY = NULL; ID_COMPET = NULL; ID_RANGE = NULL; LIFTER_SEX = NULL; LIFTER_DATEBITH = NULL; LEVLOW = NULL; LEVHIGH = NULL; FIX = NULL; LVL = NULL; END^ SET TERM ; ^

SET TERM ^ ;
CREATE OR ALTER procedure COMPETSTAT (
    IDC numeric(12,0))
returns (
    NAME varchar(150),
    "VALUE" varchar(250))
as
begin
  NAME = 'Кол-во спортсменов общее';
  SELECT COUNT(ID_LIFTER_COMPET)
  FROM LIFTER_ON_COMPETITION
  WHERE ID_COMPET = :"IDC"
  INTO :"VALUE";
  suspend;

  NAME = 'Кол-во спортсменов уник.';
  SELECT COUNT(distinct ID_LIFTER)
  FROM LIFTER_ON_COMPETITION
  WHERE ID_COMPET = :IDC
  INTO :"VALUE";
  suspend;

  NAME = 'Кол-во команд участниц';
  SELECT COUNT(distinct ID_TEAM)
  FROM LIFTER_ON_COMPETITION
  WHERE ID_COMPET = :IDC
  INTO :"VALUE";
  suspend;

  NAME = 'Кол-во городов спортсменов';
  select count(distinct(l.town))
  from lifter_on_competition loc join
    Lifter L on l.id_lifter = loc.id_lifter
  where loc.id_compet = :IDC
  into :"VALUE";
  suspend;
  
  NAME = 'Кол-во рекордов национальных';
  select NUM from RECORDSINCOMPET(:IDC, 1) into :"VALUE";
  suspend;

  NAME = 'Кол-во рекордов европейских';
  select NUM from RECORDSINCOMPET(:IDC, 2) into :"VALUE";
  suspend;

  NAME = 'Кол-во рекордов мировых';
  select NUM from RECORDSINCOMPET(:IDC, 3) into :"VALUE";
  suspend;

  NAME = 'Спортсмены - самый пожилой';
  select TRIM(l.name)||'('||
    ((cast('now' as date) - l.dat_bith)/365)||
    ') '||', '||TRIM(l.town)
  from lifter_on_competition loc join
    Lifter L on l.id_lifter = loc.id_lifter
  where loc.id_compet = :IDC
  order by l.dat_bith asc
  rows 1 
  into :"VALUE";
  suspend;

  NAME = 'Спортсмены - самый молодой';
  select  TRIM(l.name)||'('||
    ((cast('now' as date) - l.dat_bith)/365)||
    ') '||', '||TRIM(l.town)
  from lifter_on_competition loc join
    Lifter L on l.id_lifter = loc.id_lifter
  where loc.id_compet = :IDC
  order by  l.dat_bith desc
  rows 1 
  into :"VALUE";
  suspend;

end

^SET TERM ; ^

SET TERM ^ ; create or alter procedure COPY_LIFTER_COMPET ( loc numeric(12), idc numeric(12), ids numeric(12), idac numeric(12), move integer) returns ( id numeric(12), ts timestamp ) AS BEGIN ts = current_timestamp;
if (coalesce(move, 0) = 1) then begin id = :loc; update lifter_on_competition set id_compet = :idc ,id_stream = :ids ,id_agecl = :idac where id_lifter_compet = :loc; end else begin merge into lifter_on_competition dest_loc using (select id_category, id_lifter, id_compet, id_wilks, id_stream, club, trener, id_team, zr, InZach, id_agecl, newRazr, wasRazr, edittime, createtime from lifter_on_competition where id_lifter_compet = :loc) as source_loc on dest_loc.id_compet = :idc and dest_loc.id_stream = :ids and dest_loc.id_agecl = :idac and dest_loc.id_lifter = source_loc.id_lifter and dest_loc.id_category = source_loc.id_category when matched then update set id_stream = :ids, id_category = source_loc.id_category, id_wilks = source_loc.id_wilks, id_team = source_loc.id_team, trener = source_loc.trener, zr = source_loc.zr, newRazr = source_loc.newRazr, wasRazr = source_loc.wasRazr, inZach = source_loc.inZach, club = source_loc.club, createtime = source_loc.createtime, edittime = :ts when not matched then insert ( id_lifter_compet, id_category, id_lifter, id_compet, id_wilks, id_stream, club, trener, id_team, zr, inzach, id_agecl, edittime, createtime) values ( gen_id(loc, 1), source_loc.id_category, source_loc.id_lifter, :idc, source_loc.id_wilks, :ids, source_loc.club, source_loc.trener, source_loc.id_team, source_loc.zr, source_loc.inzach, :idac, :ts, :ts);
select loc2.id_lifter_compet from lifter_on_competition loc1, lifter_on_competition loc2 where loc1.id_lifter_compet = :loc and loc1.id_lifter=loc2.id_lifter and loc2.id_compet = :idc and loc2.id_stream = :ids and loc2.id_agecl = :idac and loc1.id_category=loc2.id_category rows 1 into :id;
merge into lifter_exersis dest_le using (select id_lifter_compet, SQUAT1, OC1, SQUAT2, OC2, SQUAT3, OC3, SQUAT4, OC10, BENCH1, OC4, BENCH2, OC5, BENCH3, OC6, BENCH4, OC11, DL1, OC7, DL2, OC8, DL3, OC9, DL4, OC12, SUMMAALL, PLACE, POINTS, STOY, EDITTIME from lifter_exersis where id_lifter_compet = :loc) as source_le on source_le.id_lifter_compet = :id when matched then update set SQUAT1 = source_le.SQUAT1, OC1 = source_le.oc1, SQUAT2 = source_le.SQUAT2, OC2 = source_le.oc2, SQUAT3 = source_le.SQUAT3, OC3 = source_le.oc3, SQUAT4 = source_le.SQUAT4, OC10 = source_le.oc10, BENCH1 = source_le.BENCH1, OC4 = source_le.oc4, BENCH2 = source_le.BENCH2, OC5 = source_le.oc5, BENCH3 = source_le.BENCH3, OC6 = source_le.oc6, BENCH4 = source_le.BENCH4, OC11 = source_le.oc11, DL1 = source_le.DL1, OC7 = source_le.oc7, DL2 = source_le.DL2, OC8 = source_le.oc8, DL3 = source_le.DL3, OC9 = source_le.oc9, DL4 = source_le.DL4, OC12 = source_le.oc12, SUMMAALL = source_le.SUMMAALL, PLACE = source_le.place, POINTS = source_le.POINTS, STOY = source_le.stoy, EDITTIME = :ts when not matched then insert (SQUAT1, OC1, SQUAT2, OC2, SQUAT3, OC3, SQUAT4, OC10, BENCH1, OC4, BENCH2, OC5, BENCH3, OC6, BENCH4, OC11, DL1, OC7, DL2, OC8, DL3, OC9, DL4, OC12, SUMMAALL, PLACE, POINTS, STOY, EDITTIME, id_lifter_compet) values (source_le.SQUAT1, source_le.OC1, source_le.SQUAT2, source_le.OC2, source_le.SQUAT3, source_le.OC3, source_le.SQUAT4, source_le.OC10, source_le.BENCH1, source_le.OC4, source_le.BENCH2, source_le.OC5, source_le.BENCH3, source_le.OC6, source_le.BENCH4, source_le.OC11, source_le.DL1, source_le.OC7, source_le.DL2, source_le.OC8, source_le.DL3, source_le.OC9, source_le.DL4, source_le.OC12, source_le.SUMMAALL, source_le.PLACE, source_le.POINTS, source_le.STOY, :ts, :id); end
